INSERT INTO `post` VALUES (29, 'js', 'JS00-æ¶ˆå¤±æŒ‰é’®', 'ä½¿ç”¨jsæ“¦å»æŒ‰é’®', '# JS00-æ¶ˆå¤±æŒ‰é’®\r\n\r\n[TOC]\r\n\r\n### æ¡ˆä¾‹: æ“¦å»ä¸€ä¸ªæŒ‰é’®\r\n\r\n##### ä¸€. html( 2 ä¸ªæŒ‰é’®)\r\n\r\n##### æ–‡ä»¶1-1: æ¶ˆå¤±ä¸€ä¸ªæŒ‰é’®-make-button-dispear.html\r\n\r\n```html\r\n<input type=\"button\" value=\"æ— è¾œçš„æŒ‰é’®\" id=\"tarBtn\" style=\"display: block;\"/>\r\n<input type=\"button\" value=\"æ¶ˆå¤±å®ƒ\" id=\"wipeBtn\" onclick=\"eg.wipe()\" />\r\n```\r\n\r\n#### äºŒ.js\r\n\r\n##### æ–‡ä»¶2-1: make-button-dispear.js\r\n\r\n```js\r\nvar eg = {}\r\n\r\neg.$ = function(id) {\r\n    return document.getElementById(id);\r\n}\r\n\r\neg.wipe = function() {\r\n    //ç¬¬ä¸€ç§æ–¹æ³•: \r\n    eg.$(\"tarBtn\").style.visibility = \'hidden\';\r\n    //ç¬¬äºŒç§æ–¹æ³•:\r\n    eg.$(\"tarBtn\").style.display = \'none\';\r\n}\r\n```\r\n\r\n\r\n\r\n#### 2 ç§æ–¹æ³•çš„å¯¹æ¯”\r\n\r\nç¬¬ä¸€ç§åªæ˜¯ä¿®æ”¹å…ƒç´ çš„\"visibility\" å±æ€§, å…ƒç´ æœ¬èº«ä»ç„¶åœ¨é‚£.\r\n\r\nç¬¬äºŒç§æ˜¯ä¿®æ”¹å…ƒç´ çš„\"display\"å±æ€§, ä¸€ä¸ªå…ƒç´ çš„\"display\"å±æ€§ä¸º\"none\"æ—¶ç›¸å½“äºå®Œå…¨æ¶ˆå¤±, ä¸å ä»»ä½•ç©ºé—´.\r\n\r\næ‰€ä»¥å‰ä¸€ç§æ˜¯æŒ‰é’® 1 æ¶ˆå¤±, ä½†ä»ç„¶ç©ºä¸€å—, åä¸€ç§æ˜¯æŒ‰é’® 1 æ¶ˆå¤± , åŸæ¥æ’åœ¨æŒ‰é’® 1 ä¸‹é¢çš„æŒ‰é’® 2 ä»£æ›¿æŒ‰é’® 1 çš„ä½ç½®.', '2020-02-02');
INSERT INTO `post` VALUES (30, 'Java', 'æ°´ä»™èŠ±æ•°', 'æ·»åŠ ä¸€æ¡ç®€ä»‹', '# other-æ°´ä»™èŠ±æ•°\r\n\r\n### ä¸€.æ¦‚å¿µ\r\n\r\næ°´ä»™èŠ±æ•°ï¼ˆNarcissistic numberï¼‰\r\n\r\nä¹Ÿè¢«ç§°ä¸ºè¶…å®Œå…¨æ•°å­—ä¸å˜æ•°ï¼ˆpluperfect digital invariant, PPDIï¼‰ã€è‡ªæ‹æ•°ã€è‡ªå¹‚æ•°ã€é˜¿å§†æ–¯å£®æ•°æˆ–é˜¿å§†æ–¯ç‰¹æœ—æ•°ï¼ˆArmstrong numberï¼‰ï¼Œ\r\n\r\næ°´ä»™èŠ±æ•°æ˜¯æŒ‡ä¸€ä¸ª 3 ä½æ•°ï¼Œ\r\n\r\nå®ƒçš„æ¯ä¸ªä½ä¸Šçš„æ•°å­—çš„ 3æ¬¡å¹‚ä¹‹å’Œç­‰äºå®ƒæœ¬èº«ï¼ˆä¾‹å¦‚ï¼š1^3 + 5^3+ 3^3 = 153ï¼‰ã€‚\r\n\r\n[æ‘˜è‡ªç™¾åº¦ç™¾ç§‘]\r\n\r\nç»™å®šä¸€å¤§ä¸€å° 2 ä¸ªæ•´æ•°ä½œä¸ºä¸€ä¸ªæ•´æ•°åŒºé—´, æ±‚è¿™ä¸ªæ•´æ•°åŒºé—´å†…çš„æ°´ä»™èŠ±æ•°.\r\n\r\n\r\n\r\n\r\n\r\n### äºŒ.é¢˜è§£\r\n\r\n#### åˆ€å‰Šé¢æ³•\r\n\r\næŠŠä¸€ä¸ªæ•°å„ä¸ªä½æ•°ä¸Šçš„æ•°å­—ä¸€ä¸ªä¸ªå‰Šä¸‹æ¥, è®¡ç®—ä¸‰æ¬¡æ–¹ç„¶åç´¯åŠ èµ·æ¥, æœ€åå’ŒåŸæ¥çš„æ•°å­—å¯¹æ¯”.\r\n\r\n\r\n\r\n##### è¿‡ç¨‹ä¸­å¯èƒ½ä¼šé‡åˆ°çš„é—®é¢˜\r\n\r\n- å¦‚ä½•å°†ä¸€ä¸ªæ¯”å¦‚ä¸‰ä½æ•°, æŠŠå„ä¸ªä½æ•°ä¸Šçš„æ•°å­—æ‹¿ä¸‹æ¥.\r\n\r\nä½™åé™¤åæ³•:\r\n\r\nä»¥ 125 ä¸¾ä¾‹, å…ˆå°† 125 ä½™å, å¾—åˆ°çš„å°±æ˜¯ 5. è¿™æ ·, æ¯æ¬¡ä½œä½™åè¿ç®—, æ€»èƒ½å¾—åˆ°ä¸ªä½æ•°, ä¹Ÿå°±æ˜¯ä¸èƒ½è¢« 10 æ•´é™¤çš„æ•°å­—.\r\n\r\næ¥ç€, å°† 125 é™¤ä»¥å. ä¹Ÿå°±æ˜¯ç¼©å°åå€, å‰”é™¤ä¸ªä½, å¾—åˆ° 25. è¿™æ ·, æ¯æ¬¡å¯¹ååšé™¤æ³•, æ€»èƒ½å‰”é™¤æ‰åä½æ•°. å¾—åˆ°å‰©ä½™çš„ä½æ•°ä¸Šçš„æ•°å­—.\r\n\r\n\r\n\r\n### ä¸‰.ä»£ç å®ç°(Java)\r\n\r\n```java\r\npackage leet.calc;\r\n\r\nimport java.util.ArrayList;\r\nimport java.util.List;\r\n\r\n/**\r\n * @name  FlowerNumber\r\n * @layer leet.calc\r\n * @intro\r\n * @function\r\n * */\r\npublic class FlowerNumber {\r\n	public static List<Integer> findFlower(int n, int m) {\r\n		List<Integer> list = new ArrayList<Integer>();\r\n		\r\n		for (int i = n; i <= m; i++) {\r\n			int temp = i;	//	å–å‡º i\r\n			int res = 0;\r\n			while (temp > 0) {\r\n				int val = temp%10;\r\n				double tVal = Math.pow(val, 3);\r\n				res += tVal;	//æ•´æ•°çš„æŒ‡æ•°è¿˜æ˜¯æ•´æ•°, æ‰€ä»¥ä¸å½±å“ç»“æœ\r\n				temp/=10;\r\n			}\r\n			if (res == (int)i) {\r\n				list.add(i);\r\n			}\r\n		}\r\n		return list;\r\n	}\r\n	public static void main(String[] args) {\r\n		int n = 152;\r\n		int m = 500;\r\n		System.out.println(findFlower(n, m));\r\n        //[153, 370, 371, 407]\r\n	}\r\n}\r\n```\r\n\r\nå¦å¤–, å¦‚æœé¢˜ç›®è¦æ±‚è¿”å›ä¸€ä¸ªæ•´å‹æ•°ç»„çš„è¯, åˆ†äº«ä¸€ä¸ªå†·é—¨çš„æ–¹æ³•.\r\n\r\n```java\r\nint[] a = list.stream().mapToInt(Integer::intValue).toArray();\r\n```\r\n\r\n\r\n\r\n', '2021-05-16');
INSERT INTO `post` VALUES (34, 'css', 'CSS-é€‰æ‹©å™¨', 'CSS-é€‰æ‹©å™¨', '# CSS-é€‰æ‹©å™¨\r\n\r\n[TOC]\r\n\r\n### æ ¼å¼\r\n\r\nä¸€æ¡ CSS è§„åˆ™ = é€‰æ‹©å™¨ + å£°æ˜å—\r\n\r\n```css\r\nh1 {\r\n    color: red;\r\n    background-color: yellow;\r\n}\r\n```\r\n\r\nå£°æ˜å— = å±æ€§ : å±æ€§å€¼. \r\n\r\nå¤šä¸ªå£°æ˜ä¹‹é—´ç”¨ `;` éš”å¼€. æœ€åä¸€æ¡çš„ ; å¯åŠ å¯ä¸åŠ (æ¨èåŠ ä¸Š, æ–¹ä¾¿æ ·å¼çš„è¡¥å……)\r\n\r\n\r\n\r\n### é€‰æ‹©å™¨\r\n\r\nå½“å¤šä¸ªå…ƒç´ åº”ç”¨ç›¸åŒä¸€ç»„æ ·å¼æ—¶, å¤šä¸ªé€‰æ‹©å™¨ä¹‹é—´ç”¨ `,` éš”å¼€\r\n\r\n```css\r\nh1, h2 {\r\n    color: red;\r\n}\r\n```\r\n\r\n\r\n\r\n### å£°æ˜å—\r\n\r\nå£°æ˜å— = å±æ€§ : å±æ€§å€¼\r\n\r\næœ‰äº›å±æ€§ä¸æ­¢ä¸€ä¸ªå±æ€§å€¼, æ¯”å¦‚è¯´ç»™ä¸€ç»„æ–‡å­—åŠ ä¸Šè¾¹æ¡†çº¿\r\n\r\nè¾¹æ¡†æœ‰ 3 ç§å±æ€§å€¼ - è¾¹æ¡†çº¿ç²—ç»†, è¾¹æ¡†çº¿ç±»å‹, è¾¹æ¡†çº¿é¢œè‰².\r\n\r\nå¤šä¸ªå±æ€§å€¼ä¹‹é—´ç”¨ ç©ºæ ¼ éš”å¼€\r\n\r\n```css\r\nh1 {\r\n    border: 1px solid red;\r\n}\r\n```\r\n\r\n\r\n\r\n ä»¥ä¸ŠåŸºæœ¬å°±æ˜¯ CSS æ ¼å¼çš„æ‰€æœ‰å†…å®¹äº†. ä¹‹åå¯ä»¥æ­£å¸¸å†™ä¸€äº›å¸¸è§çš„ CSS äº†.\r\n\r\nCSS çš„åº”ç”¨è§ CSS-style.', NULL);
