INSERT INTO `post` VALUES (46, 'Java', 'Java03-å­—ç¬¦ä¸²', '', '# Java03-å­—ç¬¦ä¸²\r\n\r\n[TOC]\r\n\r\n##### å­—ç¬¦ä¸²åè½¬\r\n\r\n```java\r\n    String s = \"abcdefg\";\r\n    System.out.println(new StringBuffer(s).reverse().toString()); //gfedcba\r\n```\r\n\r\nåº”ç”¨: å€¼ä¸ºå‡åºæ•°å­—ä¸²ç°åœ¨è¦è½¬åŒ–æˆå€’åºæ•°å­—ä¸²\r\n\r\n\r\n\r\n\r\n\r\n##### char & String\r\n\r\nåˆ¤æ–­å­—ç¬¦ä¸²ä¸­æ˜¯å¦å«æœ‰æŸä¸ªå­—ç¬¦, å¦‚æœæœ‰çš„è¯è·å–å…¶æ‰€åœ¨çš„ç´¢å¼•\r\n\r\n> api: int indexOf(String str) ï¼šè¿”å›ç¬¬ä¸€æ¬¡å‡ºç°çš„æŒ‡å®šå­å­—ç¬¦ä¸²åœ¨æ­¤å­—ç¬¦ä¸²ä¸­çš„ç´¢å¼•ä½ç½®ã€‚ \r\n\r\n```java\r\npackage chao3.no8;\r\n\r\n/**\r\n * @name  StrGotChar\r\n * @layer chao3.no8\r\n * @intro	è¿”å›å­—ç¬¦åœ¨å­—ç¬¦ä¸²ä¸­çš„ä½ç½®, é¡ºä¾¿åˆ¤æ–­æ˜¯å¦åŒ…å«è¯¥å­—ç¬¦\r\n * @function\r\n * */\r\npublic class StrGotChar {\r\n	public static void main(String[] args) {\r\n		//ä¸€ä¸ªå­—ç¬¦ä¸²\r\n		String mom = \"abcdee\";\r\n		char c = \'e\';						//å‚æ•°ä¼ å…¥ c\r\n		System.out.println(mom.indexOf(c));	//4\r\n	}\r\n}\r\n```\r\n\r\n\r\n\r\n##### int & String\r\n\r\næ•°å€¼å‹çš„å­—ç¬¦ä¸², è·å–å…¶ä¸­çš„å­—é¢å€¼, ç”¨ `Integer`çš„æå–æœ¯ valueOf\r\n\r\n```java\r\npackage chap3.str;\r\n\r\n/**\r\n * @name  StringToInt\r\n * @layer chap3.no5\r\n * @intro	è¾“å‡ºæ•°å­—å‹å­—ç¬¦ä¸²é‡Œçš„å­—é¢å€¼\r\n * @function	ä»‹ç»æ–¹æ³•\r\n * */\r\npublic class StringToInt {\r\n	\r\n	public static void main(String[] args) {\r\n		String a = \"1234\";\r\n		int aVal = Integer.valueOf(a);	//1234\r\n		System.out.println(aVal); 		//1234		\r\n	}	\r\n}\r\n```\r\n\r\n\r\n\r\n\r\n\r\n##### String & int\r\n\r\næ•°å­—è½¬å­—ç¬¦ä¸² 2 ç§æ–¹æ³•\r\n\r\n1.å­—ç¬¦ä¸²æ‹¼æ¥\r\n\r\n2.`String` çš„æå–æœ¯ valueOf\r\n\r\n```java\r\nstrArr[i] = String.valueOf(nums[i]);\r\nstrArr[i] = nums[i] + \"\";\r\n```\r\n\r\n\r\n\r\n\r\n\r\n```\r\nåè€…æ›´å¿«, åŒä¸€ä¸ªç®—æ³•, åªä¿®æ”¹è¯¥éƒ¨åˆ†çš„å®ç°, å‰è€…æ‰§è¡Œ 13 ms, åè€…æ‰§è¡Œ 6ms (å‚è€ƒ leetcode179)\r\n```\r\n\r\n\r\n\r\n##### compareTo\r\n\r\nä¸¤ä¸ªå­—ç¬¦ä¸²ä¹‹é—´ compareTo, è¿”å›å¼€å¤´å­—ç¬¦ ASCII ç å€¼ç›¸å‡çš„å·®å€¼.\r\n\r\n```java\r\npackage chap3.str;\r\n\r\n/**\r\n * @name  CompareTo\r\n * @layer chap3.str\r\n * @intro	compareTo():int å¼€å¤´çš„å­—ç¬¦ç›¸è§çš„å·®å€¼\r\n * @function\r\n * */\r\npublic class CompareTo {\r\n	public static void main(String[] args) {\r\n		String s1 = \"abc\";\r\n		String s2 = \"bcd\";\r\n		System.out.println(s1.compareTo(s2));	//-1\r\n		String s3 = \"g\";\r\n        //dbdefg\r\n		System.out.println(s2.compareTo(s3));   //-5\r\n	}\r\n}\r\n```\r\n\r\n\r\n\r\n##### å¸¸é‡åœ¨å¸¸é‡æ± é‡Œçš„å¤ç”¨\r\n\r\nå­—ç¬¦ä¸²å¸¸é‡åœ¨å¸¸é‡æ± ä¸­ç¼“å­˜, å½“é‡åˆ°ç›¸åŒçš„å€¼æ—¶, å¤ç”¨æ± é‡Œçš„å˜é‡.\r\n\r\n```java\r\npackage chap3.str;\r\n\r\n/**\r\n * @name  ChangLiang\r\n * @layer chap3.str\r\n * @intro	å­—ç¬¦ä¸²å¸¸é‡\r\n * @function	å­—ç¬¦ä¸²å¸¸é‡ç”¨å¸¸é‡ç¼“å­˜, å¦‚æœå­—ç¬¦ä¸²å¸¸é‡å€¼é‡å¤(å¸¸é‡æ‹¼æ¥ä¹Ÿç®—), åˆ™å¤ç”¨ä¸Šä¸€ä¸ªå¸¸é‡\r\n * */\r\npublic class ChangLiang {\r\n	public static void main(String[] args) {\r\n		String s1 = \"hello\";\r\n		String s2 = \"he\" + \"llo\";	//å¤šä¸ªå¸¸é‡æ‹¼æ¥è¿˜æ˜¯å¸¸é‡\r\n		String s3 = \"hello\";\r\n		\r\n		//æŸ¥çœ‹ä»¥ä¸Š \"3\" ä¸ªå˜é‡çš„å†…å­˜åœ°å€ \r\n		System.out.println(s1==s2);\r\n		System.out.println(s1==s3);\r\n		//è¾“å‡º\r\n		//true\r\n		//true\r\n		\r\n		String s4 = \"he\";\r\n		String s5 = \"llo\";				\r\n		String s6 = s4+s5;\r\n		System.out.println(s6);\r\n		\r\n		System.out.println(s1 == s6);\r\n		//è¾“å‡º\r\n		//false				\r\n	}\r\n}\r\n```\r\n\r\nå†…å­˜\r\n\r\nJava ä¼šç¡®ä¿æ¯ä¸ªå­—ç¬¦ä¸²å¸¸é‡åªæœ‰ä¸€ä¸ª, ä¸ä¼šäº§ç”Ÿå¤šä¸ªå‰¯æœ¬, å½“æœ‰åŒä¸€ä¸ªå€¼çš„å­—ç¬¦ä¸²è¢«å¼•ç”¨æ—¶, ä¼˜å…ˆä½¿ç”¨å¸¸é‡æ± å†…å·²æœ‰çš„å˜é‡, ä¸å¦å¤–å¼€è¾Ÿç©ºé—´.\r\n\r\nç–¯ç‹‚ Java è®²ä¹‰ 3.6.2. ç›´æ¥é‡çš„èµ‹å€¼ | æç¤º', '2021-04-26');
INSERT INTO `post` VALUES (47, 'Java', 'Java-3-å¸¸ç”¨è¿ç®—', '', '# Java-3-å¸¸ç”¨è¿ç®—\r\n\r\n\r\n\r\nç»å¯¹å€¼\r\n\r\n`Math.abs()`\r\n\r\n```java\r\npackage chap3.calc;\r\n\r\n/**\r\n * @name  MathAbd\r\n * @layer chap3.calc\r\n * @intro	æ±‚ç»å¯¹å€¼\r\n * @function\r\n * */\r\npublic class MathAbs {\r\n	public static void main(String[] args) {\r\n		System.out.println(Math.abs(-2));	//2\r\n		System.out.println(Math.abs(12-30));	//18\r\n	}\r\n}	\r\n```\r\n\r\nåº”ç”¨: æ±‚å·®å€¼\r\n\r\n\r\n\r\néšæœºæ•°\r\n\r\n`Math.random()`\r\n\r\nç”Ÿæˆ 0-1 ä¹‹é—´çš„éšæœºå°æ•°. \r\n\r\néšæœºæ•°å’Œéšæœºæ•°çš„å˜å½¢\r\n\r\n```java\r\n//ç”Ÿæˆ 0~1 ä¹‹é—´çš„éšæœºæ•°\r\nSystem.out.println(Math.random()); 			//0.45739820511766016\r\n//ç”Ÿæˆ 1~10 ä¹‹é—´çš„éšæœºæ•°\r\nSystem.out.println(Math.random()*10); 		//7.014110425875772\r\nSystem.out.println((int) (Math.random()*10 + 98));	//104\r\nSystem.out.println((int) (Math.random()*10 + 65));	//68\r\nSystem.out.println((char)(int) (Math.random()*10 + 98));	//j	\r\nSystem.out.println((char)(int)(Math.random()+65));	//A\r\n```\r\n\r\n\r\n\r\n\r\n\r\nn æ¬¡æ–¹è¿ç®—\r\n\r\n`Math.pow(a, b)`\r\n\r\nè¿”å›ç±»å‹æ˜¯ `double`\r\n\r\n```java\r\nSystem.out.println(Math.pow(1, 3) + Math.pow(5, 3) + Math.pow(3, 3));	//153.0\r\n```\r\n\r\n', '2021-04-26');
INSERT INTO `post` VALUES (48, 'Java', 'Java05-Javaæ ¸å¿ƒ', '', '# Java05-Javaæ ¸å¿ƒ\r\n\r\n[TOC]\r\n\r\n#### *public æ–¹æ³•æ‰èƒ½è·¨åŒ…è®¿é—®\r\n\r\näº‹æƒ…æ˜¯è¿™æ ·çš„: \r\n\r\n```\r\næˆ‘åœ¨ `service` ä¸­å†™äº† è°ƒç”¨ `dao` ä¸­çš„æ·»åŠ ç”¨æˆ·æ–¹æ³• `addMan(String name)`\r\nä½†æ˜¯åœ¨ `controller` ä¸­è°ƒç”¨ `service` æ‰§è¡Œè¯¥æ–¹æ³•çš„åœ°æ–¹, æç¤º `Cannot be accessed from outside package`\r\næç¤º \'make method in service public\'\r\n```\r\n\r\nè¿™æ˜¯åŸ `service` ä¸­çš„ `add ` æ–¹æ³•\r\n\r\n```java\r\n@Service\r\npublic class ManService {\r\n\r\n    @Autowired\r\n    ManMapper manMapper;\r\n\r\n    //public\r\n    void addMan(String name) {\r\n        manMapper.addMan(name);\r\n    }\r\n}\r\n```\r\n\r\n\r\n\r\n### ç±»å†…é‡è½½\r\n\r\nä¾‹å­: `a+b` å¯ä»¥æœ‰çš„çš„é‡è½½å½¢å¼\r\n\r\n```java\r\npublic æ„é€ å™¨(..){}\r\npublic int sum(int a, int b);\r\npublic int sum(int a, int b, int c);\r\npublic double sum(double a, double b);\r\npublic double sum(double a, double a, double);\r\n```\r\n\r\nè®¿é—®çš„æ—¶å€™æ—¢å¯ä»¥è°ƒç”¨è®¡ç®— 2 ä¸ªæ•´æ•°çš„åŠ æ³•, è¿˜å¯ä»¥æ˜¯ 3 ä¸ªæ•´æ•°çš„åŠ æ³•. \r\n\r\nä¸ä»…å¦‚æ­¤, è¿˜å¯ä»¥è®¡ç®— 2 ç”šè‡³ 3 ä¸ªæ•´æ•°çš„åŠ æ³•. \r\n\r\nå¤–ç•Œçœ‹ä¸Šå»å¥½åƒä¸€ä¸ªæ–¹æ³•åå¯ä»¥ç”¨åœ¨ä¸åŒå‚æ•°, ä¸åŒç±»å‹çš„å‚æ•°è¿ç®—(å¤„ç†)ä¸€æ ·.\r\n\r\nåŸåˆ™: åŒåä¸åŒå‚.\r\n\r\næ— å…³å› ç´ : è¿”å›å€¼ç±»å‹æ˜¯æ— å…³çš„, æ–¹æ³•ä¿®é¥°ç¬¦æ˜¯æ— å…³çš„.\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n### è·¨ç±»é‡å†™\r\n\r\né‡å†™, åˆå«è¦†ç›–.\r\n\r\né€šè¿‡ç»§æ‰¿çˆ¶ç±», å†™å‡ºå­ç±»å®šåˆ¶åŒ–çš„æ–¹æ³•.\r\n\r\n```java\r\nclass Papa {\r\n    public void say() {\r\n        System.out.println(\"æˆ‘ä¼šè·‘\");\r\n    }\r\n}\r\n```\r\n\r\n```java\r\nclass OffSpring extends Papa{\r\n    @Override\r\n    public void say() {\r\n		System.out.println(\"æˆ‘ä¼šé£\");\r\n    }\r\n}\r\n```\r\n\r\nè¯´æ˜\r\n\r\n```\r\nä»ç»§æ‰¿çš„è§’åº¦æ¥è¯´, å­ç±»é»˜è®¤ç»§æ‰¿æ‹¥æœ‰çˆ¶ç±»çš„æ–¹æ³•. å­ç±»çš„ç±»ä½“å¯ä»¥ä¸ºç©º, å¯¹è±¡åˆå¯ä»¥è°ƒç”¨å‡ºçˆ¶ç±»é‡Œå·²æœ‰çš„æ–¹æ³•.\r\nä»ä¸°å¯Œæ€§æ¥è¯´, å­ç±»å¯ä»¥å†™åˆ«çš„æ–¹æ³•, åœ¨å­ç±»ä¸­å†™çˆ¶ç±»é‡Œæ²¡æœ‰å£°æ˜çš„æ–¹æ³•, ä»è€Œæ„é€ ä¸€ä¸ªæ›´åŠ ä¸°å¯Œçš„ç±».\r\nä¸ä»…å¦‚æ­¤, å­ç±»è¿˜å¯ä»¥é‡å†™çˆ¶ç±»çš„æ–¹æ³•, å®šåˆ¶åŒ–è‡ªå·±çš„æ–¹æ³•, è¿™ä¹Ÿæ˜¯ä¸€ç§å¢å¼ºä¸°å¯Œåº¦çš„æ–¹å¼(å¯ä»¥å¤šå†™, å¯ä»¥å†™è‡ªå·±çš„)\r\nå‰ææ˜¯, ä»…é™äºç›¸åŒçš„æ–¹æ³•åŸå‹. ä¹Ÿå°±æ˜¯å­ç±»**å¯ä»¥å†™åˆ«çš„æ–¹æ³•, ä¸èƒ½å’ŒåŸçˆ¶ç±»æ–¹æ³•é‡å**.\r\né‡å†™çš„å‰ææ˜¯**å¤åˆ¶çˆ¶ç±»çš„æ–¹æ³•åŸå‹, ä¿®æ”¹æ–¹æ³•ä½“.\r\n```\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n### æŠ½è±¡ç±»â€”â€”ç‰¹æ®Šçš„çˆ¶ç±»\r\n\r\nå®ƒ, å¯ä»¥æœ‰**ä¸å®ç°æ–¹æ³•ä½“**çš„æ–¹æ³•, å¯ä»¥åƒçˆ¶ç±»ä¸€æ ·æœ‰å±æ€§, æœ‰æ–¹æ³•, ä¹Ÿéƒ½èƒ½åƒå¹³å¸¸çˆ¶ç±»ä¸€æ ·è¢«ç»§æ‰¿. ä½†å”¯ä¸€çš„ç¼ºé™·æ˜¯, å®ƒä¸èƒ½å®ä¾‹åŒ–, ä¹Ÿå°±æ˜¯, ä¸èƒ½æ‹¥æœ‰è‡ªå·±çš„å¯¹è±¡.\r\n\r\nå®ƒçš„å¯¹è±¡å¿…é¡»ç”±å®ç°çš„éæŠ½è±¡ç±»æ„é€ .\r\n\r\n```\r\nAbstraPapa man = new Son();\r\n```\r\n\r\n2021-04-07 \r\n\r\næ–¹æ³•ä½“\r\n\r\n```java\r\n{\r\n	//...\r\n}\r\n```\r\n\r\næŠ½è±¡æ–¹æ³•\r\n\r\n```java\r\npublic abstract void go();\r\n//æ²¡æœ‰æ–¹æ³•ä½“çš„æ–¹æ³•å«åšæŠ½è±¡æ–¹æ³•\r\n\r\n```\r\n\r\næŠ½è±¡ç±»&æŠ½è±¡æ–¹æ³•\r\n\r\n```\r\næ™®é€šç±»å£°æ˜ä¸€ä¸ªæ–¹æ³•è¦ä¸ add body è¦ä¹ˆæé†’å£°æ˜ add abstract\r\n\r\nè¯´æ˜, æ­£å¸¸ç±»å¦‚æœå£°æ˜æ–¹æ³•ä¸€å®šè¦æœ‰æ–¹æ³•ä½“.\r\næ²¡æœ‰æ–¹æ³•ä½“çš„ç±»è¢«åŠ¨å£°æ˜ç§°æŠ½è±¡ç±».(åªæœ‰æŠ½è±¡ç±»æ‰èƒ½æ‹¥æœ‰æŠ½è±¡æ–¹æ³•, æ­£å¸¸ç±»è¯¥æœ‰çš„è¿˜æ˜¯è¦æœ‰)\r\n\r\n```\r\n\r\n\r\n\r\næŠ½è±¡ç±»&æ¥å£çš„ç›¸åŒç‚¹\r\n\r\n1.éƒ½ä¸èƒ½è‡ªå·±åˆ›å»ºå®ä¾‹: æŠ½è±¡ç±»è¦ä¾èµ–éæŠ½è±¡çš„å­ç±», æ¥å£è¦ä¾èµ–å®ç°ç±».\r\n\r\n\r\n\r\n\r\n\r\n#### å…¶å®ƒ\r\n\r\n```\r\njavaæ²¡æœ‰æŒ‡é’ˆæœºåˆ¶â€”â€”ç‰›å®¢\r\n\r\n```\r\n\r\n\r\n\r\n\r\n\r\n### æ¥å£\r\n\r\n> Cannot reduce the visibility of the inherited method from Interface\r\n\r\nå‡å¦‚æ¥å£ä¸­çš„æ–¹æ³•å¯è§æ€§æ˜¯ `public` , å®ç°åªèƒ½æ‰©å±•ä¸èƒ½é™ä½å¯è§æ€§.\r\n\r\nå¦‚\r\n\r\n```\r\næ¥å£æ–¹æ³• f() æ˜¯ int f();\r\nå®ç°ç±»ä¸­ f() ä¸èƒ½æ˜¯ int f();\r\nä½†å¯ä»¥æ˜¯ public int f();\r\n\r\n```\r\n\r\nå·²ä¸ŠæœºéªŒè¯.\r\n\r\n\r\n\r\n[ç‰›å®¢](https://m.nowcoder.com/questions?uuid=a7079fcaacef419c9bcbf2bf407cb3da)', '2021-04-26');
INSERT INTO `post` VALUES (49, 'Java', 'æ„é€ å™¨', '', '## æ„é€ å™¨\r\n\r\n[TOC]\r\n\r\nä¾‹å­:  Dog & DogTest\r\n\r\n```java\r\npublic class Dog {	\r\n	int eyeNum;\r\n	\r\n	Dog(int n) {\r\n		eyeNum = n;\r\n		System.out.println(\"æ„é€ å™¨åˆå§‹åŒ–\");\r\n	}\r\n	\r\n	public void run() {\r\n		System.out.println(\"å¼€å§‹è·‘\");\r\n	}\r\n}\r\n```\r\n\r\n```java\r\npublic class DogTest {\r\n	public static void main(String[] args) {\r\n		Dog dog = new Dog(2);\r\n		dog.run();\r\n		//æ„é€ å™¨åˆå§‹åŒ–\r\n		//å¼€å§‹è·‘\r\n	}\r\n}\r\n```\r\n\r\n\r\n\r\næ„é€ å™¨\r\n\r\næ„é€ å™¨æ˜¯å’Œç±»åŒåçš„ç‰¹æ®Šæ–¹æ³•, çœ‹ä¸Šå»æ²¡æœ‰è¿”å›å€¼(å†™æ³•ä¸Š, å®é™…ä¸Šä¼šè¿”å›ä¸€ä¸ªå¯¹è±¡, è¿™ä¹Ÿæ˜¯å¦ä¸€ä¸ªå’Œæ™®é€šæ–¹æ³•ä¸ä¸€æ ·çš„åœ°æ–¹).\r\n\r\né€šå¸¸ç”¨äºæ„é€ ç±»å¯¹è±¡, ç”¨ `new` è°ƒç”¨.\r\n\r\n\r\n\r\næ„é€ å™¨é‡è½½\r\n\r\næ¯ä¸ªç±»éƒ½æœ‰ä¸€ä¸ªé»˜è®¤çš„æ— å‚æ„é€ å™¨, ç”¨äºåˆ›å»ºå¯¹è±¡.\r\n\r\nä¸€ç»é‡è½½, ä¾¿è¢«è¦†ç›–.\r\n\r\n\r\n\r\n```\r\nå¦‚æœå¤šä¸ªé‡è½½çš„æ„é€ å™¨é‡ŒåŒ…å«äº†ç›¸åŒçš„åˆå§‹åŒ–ä»£ç , å¯ä»¥å°†è¿™äº›åˆå§‹åŒ–ä»£ç æ”¾ç½®åˆ°æ™®é€šçš„åˆå§‹åŒ–å—é‡Œå®Œæˆ\r\nåˆå§‹åŒ–å—æ€»æ˜¯æ„é€ å™¨æ‰§è¡Œä¹‹å‰è¢«è°ƒç”¨\r\n```\r\n\r\n\r\n\r\n\r\n\r\n##### æ¯”æ„é€ å™¨æå‰æ‰§è¡Œçš„ä»£ç å—\r\n\r\næ™®é€šåˆå§‹åŒ–å—\r\n\r\n```\r\n{\r\n	System.out.println(\"æ‰§è¡Œ..\");\r\n}\r\n```\r\n\r\næ™®é€šä»£ç å—çš„æ‰§è¡Œåœ¨å¯¹è±¡åŠ è½½ä¹‹å, æ„é€ å™¨æ‰§è¡Œä¹‹å‰è¢«è°ƒç”¨. å¦‚:\r\n\r\n```java\r\npublic class CodeBlock {			\r\n	\r\n	{		\r\n		System.out.println(\"ä»£ç å—å†…é‡Œçš„æ‰§è¡Œè¯­å¥\");\r\n	}\r\n	\r\n	//æ„é€ å™¨\r\n	CodeBlock() {\r\n		System.out.println(\"æ„é€ å™¨é‡Œçš„æ‰§è¡Œè¯­å¥\");\r\n	}\r\n}\r\n```\r\n\r\n```java\r\npublic class CodeBlockUse {\r\n	public static void main(String[] args) {\r\n		CodeBlock cb = new CodeBlock();\r\n       //è¾“å‡º\r\n		//ä»£ç å—å†…é‡Œçš„æ‰§è¡Œè¯­å¥\r\n		//æ„é€ å™¨é‡Œçš„æ‰§è¡Œè¯­å¥\r\n	}\r\n}	\r\n```\r\n\r\nçŒœæƒ³: æ„é€ å™¨é€šå¸¸æ˜¯ç”¨ä½œæˆå‘˜åˆå§‹åŒ–çš„, æ—¢ç„¶æ™®é€šä»£ç å—æ‰§è¡Œåœ¨æ„é€ å™¨ä¹‹å‰, é‚£ä¸ºä»€ä¹ˆä¸ç”¨å®ƒå…ˆæŠŠåˆå§‹åŒ–æˆå‘˜å‘¢?\r\n\r\néªŒè¯: åœ¨ `CodeBlock` ä¸­å¢åŠ  `n` æˆå‘˜, å¹¶åœ¨æ™®é€šä»£ç å—é‡Œåˆå§‹åŒ–.\r\n\r\n```java\r\npublic class CodeBlock {\r\n	\r\n	//å‡è®¾ CodeBlock æœ‰ä¸€ä¸ªå±æ€§ n ç­‰å¾…åˆå§‹åŒ–\r\n	int n;	\r\n	\r\n	{\r\n		n = 2;				\r\n		System.out.println(\"ä»£ç å—å†…é‡Œçš„æ‰§è¡Œè¯­å¥\");\r\n	}\r\n	\r\n	//æ„é€ å™¨\r\n	CodeBlock() {\r\n		System.out.println(\"æ„é€ å™¨é‡Œçš„æ‰§è¡Œè¯­å¥\");\r\n	}\r\n}\r\n```\r\n\r\n```java\r\npublic class CodeBlockUse {\r\n	public static void main(String[] args) {\r\n		CodeBlock cb = new CodeBlock();\r\n		//è¾“å‡º\r\n		//ä»£ç å—å†…é‡Œçš„æ‰§è¡Œè¯­å¥\r\n		//æ„é€ å™¨é‡Œçš„æ‰§è¡Œè¯­å¥\r\n		System.out.println(\"cdå¯¹è±¡çš„nä¸º: \" + cb.n);	\r\n        //è¾“å‡º\r\n        //cdå¯¹è±¡çš„nä¸º: 2\r\n	}\r\n}	\r\n```\r\n\r\nåˆå§‹åŒ–å—&åˆå§‹åŒ–å—çš„åº”ç”¨', '2021-04-26');
